from subprocess import check_output


def make_install_str(name, version, url, separator):
    install_str = ''
    if url is not None:
        install_str = url
    else:
        install_str = name
        if version is not None:
            install_str += separator + version
    return install_str


def git_log():
    return check_output(['git', 'log', '--format=%s']).splitlines()
